mapper.select:
select * from sp_door where 1=1

	AND id = #{id}


	AND door_name = #{doorName}


	AND door_no = #{doorNo}


	AND door_type = #{doorType}


	AND room_id = #{roomId}


	AND floor_id = #{floorId}


	AND company_id = #{companyId}


	AND dept_id = #{deptId}


	AND status = #{status}


	AND remark = #{remark}


	AND ext1 = #{ext1}


	AND ext2 = #{ext2}


	AND ext3 = #{ext3}


	AND door_readno_id = #{doorReadnoId}



		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from sp_door where 1=1

	AND id = #{id}


	AND door_name = #{doorName}


	AND door_no = #{doorNo}


	AND door_type = #{doorType}


	AND room_id = #{roomId}


	AND floor_id = #{floorId}


