mapper.select:
		select * from sp_visitor_reservation where 1=1

				AND id = #{id}


			AND phone = #{phone}


				AND reservation_no = #{reservationNo}


				AND photo_url = #{photoUrl}


				AND visitor_id = #{visitorId}


				AND staff_id = #{staffId}


				AND staff_mobile = #{staffMobile}


				AND status = #{status}


				AND appoint_start_time = #{appointStartTime}


				AND appoint_end_time = #{appointEndTime}


				AND act_start_time = #{actStartTime}


				AND act_end_time = #{actEndTime}


				AND type = #{type}


				AND physical_card_id = #{physicalCardId}


				AND open_id = #{openId}



                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT r.id, r.status, r.appoint_start_time AS appointStartTime, r.appoint_end_time AS appointEndTime, r.remark, v.name AS visitorName, v.phone AS visitorMobile, v.idcard_no AS idcardNo, r.reservation_no AS reservationNo, s.USERNAME AS staffName, s.mobile AS staffMobile FROM sp_visitor_reservation r INNER JOIN sp_visitor_info_his v ON v.reservation_id = r.id INNER JOIN base_staff s ON s.id = r.staff_id
WHERE r.open_id = #{openId}
INNER JOIN tb_user u ON u.mobile = s.MOBILE WHERE u.open_id = #{openId}
AND r.status = #{status}
ORDER BY r.create_time desc
limit #{offset}, #{limit}

		SELECT COUNT(1) FROM sp_visitor_reservation r
        INNER JOIN sp_visitor_info_his v ON v.reservation_id = r.id

			WHERE r.open_id = #{openId}


			INNER JOIN base_staff s ON s.id = r.staff_id
			INNER JOIN tb_user u ON u.mobile = s.MOBILE
			WHERE u.open_id = #{openId}


			AND r.status = #{status}

		ORDER BY r.create_time desc

		select count(*) from sp_visitor_reservation where 1=1

                AND id = #{id}


                AND reservation_no = #{reservationNo}


                AND phone = #{phone}


                AND photo_url = #{photoUrl}


                AND visitor_id = #{visitorId}


                AND staff_id = #{staffId}


				AND staff_mobile = #{staffMobile}


                AND status = #{status}


                AND appoint_start_time = #{appointStartTime}


                AND appoint_end_time = #{appointEndTime}


                AND act_start_time = #{actStartTime}


                AND act_end_time = #{actEndTime}


                AND type = #{type}


                AND physical_card_id = #{physicalCardId}

SELECT COUNT(1) FROM sp_visitor_reservation r INNER JOIN sp_visitor_info_his v ON v.reservation_id = r.id INNER JOIN base_staff s ON s.id = r.staff_id WHERE 1=1
AND (v.name = #{keyword} or v.phone = #{keyword} or v.idcard_no = #{keyword} or s.USERNAME = #{keyword} or s.mobile = #{keyword})
AND r.status = #{status}

SELECT r.id, r.status, r.appoint_start_time AS appointStartTime, r.appoint_end_time AS appointEndTime, r.remark, v.name AS visitorName, v.phone AS visitorMobile, v.idcard_no AS idcardNo, r.reservation_no AS reservationNo, s.USERNAME AS staffName, s.mobile AS staffMobile FROM sp_visitor_reservation r INNER JOIN sp_visitor_info_his v ON v.reservation_id = r.id INNER JOIN base_staff s ON s.id = r.staff_id WHERE 1=1
AND (v.name = #{keyword} or v.phone = #{keyword} or v.idcard_no = #{keyword} or s.USERNAME = #{keyword} or s.mobile = #{keyword})
AND r.status = #{status}
ORDER BY r.create_time desc
limit #{offset}, #{limit}
