mapper.select:
		select * from ibms_dev_collection_record where 1=1

				AND id = #{id}


				AND device_id = #{deviceId}


				AND subsystem_id = #{subsystemId}


				AND attr_id = #{attrId}


				AND value = #{value}


				AND collection_date = #{collectionDate}



                order by ${sidx} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select count(*) from ibms_dev_collection_record where 1=1

                AND id = #{id}


                AND device_id = #{deviceId}


                AND subsystem_id = #{subsystemId}


                AND attr_id = #{attrId}


                AND value = #{value}


                AND collection_date = #{collectionDate}
